contracts/ChargeHandler.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Charge, ChargeType} from "./library/Charge.sol";

/**
 * @dev The ChargeHandler contract is an internal library used by the Gatekeeper
 * to handle charges made to the gatekeeper on gateway token issuance or refresh.
 *
 * The charge functionality operates in conjunction with the forwarder to allow a transaction to be
 * a) signed by a gatekeeper, but
 * b) sent from a gateway token recipient
 * This is necessary for the ETH charge type, as the charge payer must sign the transaction. For ERC20
 * transactions, it is sufficient for the charge payer to sign an approval transaction beforehand.
 *
 * Note: The sender address of the charge can differ between the ETH and ERC20 charge types.
 * ETH: The funds are sent via the "value" property of an eth transaction, and are forwarded on to the
 * recipient address.
 * ERC20: The sender address is specified as `tokenSender` in the Charge object. This will typically be,
 * the gateway token recipient, but it can be any address. Setting this value explicitly is necessary, as
 * it cannot be reliably derived from within the GatewayToken contract. Options would be:
 * _msgSender() (ERC2771) - This is the inner transaction sender - the gatekeeper
 * msg.sender - This is the forwarder contract
 * tx.origin - This is the EOA (Externally Owned Account) that signed the outer transaction. While this is
 * typically the gateway token recipient, using tx.origin precludes the use of smart contract wallets, as well as
 * being discouraged for other security reasons.
 */
contract ChargeHandler {
    event ChargePaid(Charge);

    error Charge__InsufficientAllowance(uint256 allowance, uint256 expectedAllowance);

    error Charge__InsufficientValue(uint256 value, uint256 expectedValue);

    error Charge__TransferFailed(uint256 value);

    /**
     * @dev Send a fee either in ETH (wei) or ERC20 to the gatekeeper.
     * Note, ERC20 requires that the sender has approved the amount.
     * This function uses the CEI (checks-effects-interactions) pattern to avoid reentrancy
     * when sending ETH to the recipient (if the recipient is a smart contract)
     * @param charge The charge details
     **/
    function _handleCharge(Charge calldata charge) internal {
        if (charge.chargeType == ChargeType.ETH) {
            // CHECKS
            // send wei if the charge type is ETH
            if (msg.value < charge.value) {
                revert Charge__InsufficientValue(msg.value, charge.value);
            }

            // EFFECTS
            emit ChargePaid(charge);

            // INTERACTIONS
            (bool success, ) = payable(charge.recipient).call{value: charge.value}("");
            if (!success) {
                revert Charge__TransferFailed(charge.value);
            }
        } else if (charge.chargeType == ChargeType.ERC20) {
            // send tokens if the charge type is ERC20
            IERC20 token = IERC20(charge.token);

            // CHECKS
            // check that the sender has approved the token transfer
            uint256 allowance = token.allowance(charge.tokenSender, address(this));
            if (allowance < charge.value) {
                revert Charge__InsufficientAllowance(allowance, charge.value);
            }

            // EFFECTS
            emit ChargePaid(charge);

            // INTERACTIONS
            bool success = token.transferFrom(charge.tokenSender, charge.recipient, charge.value);
            if (!success) {
                revert Charge__TransferFailed(charge.value);
            }
        }
    }
}

contracts/FlagsStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IFlagsStorage} from "./interfaces/IFlagsStorage.sol";
import {BitMask} from "./library/BitMask.sol";
import {Common__NotSuperAdmin, Common__MissingAccount} from "./library/CommonErrors.sol";

/**
 * @dev FlagsStorage is the main contract to store KYC-related flags for Gateway Token System.
 * KYC flags are identifiable by short identifiers in bytes32 strings. After adding flags
 * those bit indexes could be used by GatewayToken implementations to associate flags per token.
 */
contract FlagsStorage is Initializable, IFlagsStorage, UUPSUpgradeable {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using Address for address;
    using BitMask for uint256;

    EnumerableSet.Bytes32Set private _supportedFlags;

    address public superAdmin;

    uint256 public supportedFlagsMask;

    mapping(bytes32 => uint8) public flagIndexes;

    /// The flag being added already exists
    /// @param flag The flag being added
    error FlagsStorage__FlagAlreadyExists(bytes32 flag);

    /// The flag being removed does not exist
    /// @param flag The flag being removed
    error FlagsStorage__FlagNotSupported(bytes32 flag);

    /// Multiple flags updated but the number of flags passed in does not match the number of indexes
    /// @param expected The number of flags passed in
    /// @param actual The number of indexes passed in
    error FlagsStorage__IncorrectVariableLength(uint256 expected, uint256 actual);

    /// The flag index is already used
    /// @param index The flag index
    error FlagsStorage__IndexAlreadyUsed(uint8 index);

    // @dev Modifier to prevent calls from anyone except Identity.com Admin
    modifier onlySuperAdmin() {
        _onlySuperAdmin();
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    // empty constructor in line with the UUPS upgradeable proxy pattern
    // solhint-disable-next-line no-empty-blocks
    constructor() {
        _disableInitializers();
    }

    function initialize(address _superAdmin) external initializer {
        if (_superAdmin == address(0)) revert Common__MissingAccount();
        superAdmin = _superAdmin;
    }

    /**
     * @dev Triggers to transfer ownership of this contract to
     * new super admin, reverts on zero address and wallet addresses
     * @param newSuperAdmin New super admin contract address
     * @notice Only executed by existing super admin
     */
    function updateSuperAdmin(address newSuperAdmin) external onlySuperAdmin {
        if (newSuperAdmin == address(0)) revert Common__MissingAccount();

        emit SuperAdminUpdated(superAdmin, newSuperAdmin);
        superAdmin = newSuperAdmin;
    }

    /**
     * @dev Triggers to add new flag into gateway token system
     * @param flag Flag short identifier
     * @param index Flag index (limited to 255)
     * @notice Only executed by existing DAO Manager
     */
    function addFlag(bytes32 flag, uint8 index) external onlySuperAdmin {
        _addFlag(flag, index);
    }

    /**
     * @dev Triggers to add multiple flags into gateway token system
     * @param flags Array of flag short identifiers
     * @param indexes Array of flag indexes (limited to 255)
     * @notice Only executed by existing DAO Manager
     */
    function addFlags(bytes32[] calldata flags, uint8[] calldata indexes) external onlySuperAdmin {
        if (flags.length != indexes.length) revert FlagsStorage__IncorrectVariableLength(flags.length, indexes.length);

        uint256 length = flags.length;
        for (uint8 i = 0; i < length; i++) {
            _addFlag(flags[i], indexes[i]);
        }
    }

    /**
     * @dev Triggers to remove existing flag from gateway token system
     * @param flag Flag short identifier
     * @notice Only executed by existing DAO Manager
     */
    function removeFlag(bytes32 flag) external onlySuperAdmin {
        if (!_supportedFlags.contains(flag)) revert FlagsStorage__FlagNotSupported(flag);

        _removeFlag(flag);
    }

    /**
     * @dev Triggers to remove multiple existing flags from gateway token system
     * @param flags Array of flag short identifiers
     * @notice Only executed by existing DAO Manager
     */
    function removeFlags(bytes32[] calldata flags) external onlySuperAdmin {
        uint256 length = flags.length;
        for (uint8 i = 0; i < length; i++) {
            // additional check to reduce incorrect FlagRemoved events
            if (!_supportedFlags.contains(flags[i])) revert FlagsStorage__FlagNotSupported(flags[i]);

            _removeFlag(flags[i]);
        }
    }

    /**
     * @dev Triggers to check if a particular flag is supported
     * @param flag Flag short identifier
     * @return Boolean for flag support
     */
    function isFlagSupported(bytes32 flag) external view returns (bool) {
        return _supportedFlags.contains(flag);
    }

    /**
     * @dev Internal function to add new flag
     */
    function _addFlag(bytes32 flag, uint8 index) internal {
        if (supportedFlagsMask.checkBit(index)) revert FlagsStorage__IndexAlreadyUsed(index);
        if (_supportedFlags.contains(flag)) revert FlagsStorage__FlagAlreadyExists(flag);

        flagIndexes[flag] = index;
        _supportedFlags.add(flag);
        supportedFlagsMask = supportedFlagsMask.setBit(index);

        emit FlagAdded(flag, index);
    }

    /**
     * @dev Internal function to remove existing flag
     */
    function _removeFlag(bytes32 flag) internal {
        _supportedFlags.remove(flag);
        uint8 _index = flagIndexes[flag];

        supportedFlagsMask = supportedFlagsMask.clearBit(_index);
        delete flagIndexes[flag];

        emit FlagRemoved(flag);
    }

    // includes the onlySuperAdmin modifier to ensure that only the super admin can call this function
    // otherwise, no other logic.
    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address) internal override onlySuperAdmin {}

    // separate into a private function to reduce code size
    function _onlySuperAdmin() private view {
        if (msg.sender != superAdmin) revert Common__NotSuperAdmin(msg.sender);
    }
}

contracts/FlexibleNonceForwarder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IForwarder} from "./interfaces/IForwarder.sol";

contract FlexibleNonceForwarder is IForwarder, EIP712, ReentrancyGuard {
    using ECDSA for bytes32;
    using Address for address payable;

    struct SigsForNonce {
        mapping(bytes => bool) sigs;
    }

    struct FlexibleNonce {
        uint256 currentNonce;
        uint256 block; // when this nonce was first used - used to age transactions
        mapping(uint256 => SigsForNonce) sigsForNonce;
    }

    bytes32 private constant _TYPEHASH =
        keccak256("ForwardRequest(address from,address to,uint256 value,uint256 gas,uint256 nonce,bytes data)");

    mapping(address => FlexibleNonce) private _nonces;

    uint256 private immutable _blockAgeTolerance;

    event ForwardResult(bool);

    /// The tx to be forwarded is not signed by the request sender.
    error FlexibleNonceForwarder__InvalidSigner(address signer, address expectedSigner);

    /// The tx to be forwarded has already been seen.
    error FlexibleNonceForwarder__TxAlreadySeen();

    /// The tx to be forwarded is too old.
    error FlexibleNonceForwarder__TxTooOld(uint256 blockNumber, uint256 blockAgeTolerance);

    constructor(uint256 blockAgeTolerance) EIP712("FlexibleNonceForwarder", "0.0.1") {
        _blockAgeTolerance = blockAgeTolerance;
    }

    function execute(
        ForwardRequest calldata req,
        bytes calldata signature
    ) external payable nonReentrant returns (bool, bytes memory) {
        _verifyFlexibleNonce(req, signature);
        _refundExcessValue(req);

        (bool success, bytes memory returndata) = req.to.call{gas: req.gas, value: req.value}(
            abi.encodePacked(req.data, req.from)
        );

        // Forward the revert message from the call if the call failed.
        if (success == false) {
            // solhint-disable-next-line no-inline-assembly
            assembly {
                revert(add(returndata, 32), mload(returndata))
            }
        }

        // Validate that the relayer has sent enough gas for the call.
        // See https://ronan.eth.limo/blog/ethereum-gas-dangers/
        if (gasleft() <= req.gas / 64) {
            // We explicitly trigger invalid opcode to consume all gas and bubble-up the effects, since
            // neither revert or assert consume all gas since Solidity 0.8.0
            // https://docs.soliditylang.org/en/v0.8.0/control-structures.html#panic-via-assert-and-error-via-require
            // solhint-disable-next-line no-inline-assembly
            assembly {
                invalid()
            }
        }
        emit ForwardResult(success);

        return (success, returndata);
    }

    function getNonce(address from) external view returns (uint256) {
        return _nonces[from].currentNonce;
    }

    function _verifyFlexibleNonce(ForwardRequest calldata req, bytes calldata signature) internal {
        address signer = _hashTypedDataV4(
            keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, req.gas, req.nonce, keccak256(req.data)))
        ).recover(signature);

        if (signer != req.from) {
            revert FlexibleNonceForwarder__InvalidSigner(signer, req.from);
        }

        if (_nonces[req.from].currentNonce == req.nonce) {
            // request nonce is expected next nonce - increment the nonce, and we are done
            _nonces[req.from].currentNonce = req.nonce + 1;
            _nonces[req.from].block = block.number;
        } else {
            // request nonce is not expected next nonce - check if we have seen this signature before
            if (_nonces[req.from].sigsForNonce[req.nonce].sigs[signature]) {
                revert FlexibleNonceForwarder__TxAlreadySeen();
            }

            // check if the nonce is too old
            if (_nonces[req.from].block + _blockAgeTolerance < block.number) {
                revert FlexibleNonceForwarder__TxTooOld(_nonces[req.from].block, _blockAgeTolerance);
            }
        }

        // store the signature for this nonce to ensure no replay attacks
        _nonces[req.from].sigsForNonce[req.nonce].sigs[signature] = true;
    }

    function _refundExcessValue(ForwardRequest calldata req) internal {
        // Refund the excess value sent to the forwarder if the value inside the request is less than the value sent.
        if (msg.value > req.value) {
            payable(msg.sender).sendValue(msg.value - req.value);
        }
    }
}

