// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

library BitMask {
    uint internal constant _ONE = uint256(1);

    /**
     * @dev Internal function to set 1 bit in specific `index`
     * @return Updated bitmask with modified bit at `index`
     */
    function setBit(uint256 self, uint8 index) internal pure returns (uint256) {
        return self | (_ONE << index);
    }

    /**
     * @dev Internal function to clear bit to 0 in specific `index`
     * @return Updated bitmask with modified bit at `index`
     */
    function clearBit(uint256 self, uint8 index) internal pure returns (uint256) {
        return self & ~(_ONE << index);
    }

    /**
     * @dev Internal function to check bit at specific `index`
     * @return Returns TRUE if bit is '1', FALSE otherwise
     */
    function checkBit(uint256 self, uint8 index) internal pure returns (bool) {
        return (self & (uint256(1) << index)) > 0;
    }
}

contracts/library/Charge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

enum ChargeType {
    NONE, // No charge
    ETH, // Charge amount is in Eth (Wei)
    ERC20 // Charge amount is in an ERC20 token (token field) in minor denomination
}

/**
 * @dev The Charge struct represents details of a charge made to the gatekeeper on
 * gateway token issuance or refresh.
 */
struct Charge {
    // the amount in either wei (if chargeType = ETH) or ERC20 minor denomination (if chargeType = ERC20)
    uint256 value;
    // whether to charge in wei or ERC20
    ChargeType chargeType;
    // if chargeType = ERC20, the token to charge in
    address token;
    // the sender of the funds if chargeType = ERC20. This is necessary as the transaction is forwarded,
    // and the originator of the transaction is not clear inside the GatewayToken contract.
    // More details in ChargeHandler.sol
    address tokenSender;
    // the recipient of the funds. Typically a wallet owned by the gatekeeper.
    address recipient;
}

contracts/library/CommonErrors.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.9;

/// The passed-in account is the zero-address
error Common__MissingAccount();

/// The passed-in account is not a contract, where an account is expected
/// @param account address of the account.
error Common__NotContract(address account);

/// The passed-in account is not a super-admin
/// @param account address of the account.
error Common__NotSuperAdmin(address account);

/// The request was made from an unauthorized address.
/// @param sender address of the sender.
/// @param domain the domain to which the role applies.
/// @param role role that is required.
error Common__Unauthorized(address sender, uint domain, bytes32 role);

contracts/MultiERC2771Context.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import {ContextUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";

/**
 * @dev Context variant with ERC2771 support for multiple trusted forwarders.
 */
abstract contract MultiERC2771Context is ContextUpgradeable {
    mapping(address => bool) private _trustedForwarders;

    function isTrustedForwarder(address forwarder) public view virtual returns (bool) {
        return _trustedForwarders[forwarder];
    }

    // because MultiERC2771Context is abstract we don't implement a
    // constructor. It's the responsibility of the derived contract to
    // disable the Initializers with "_disableInitializers()"

    // solhint-disable-next-line func-name-mixedcase
    function __MultiERC2771Context_init(address[] calldata trustedForwarders) internal initializer {
        __Context_init_unchained();
        __MultiERC2771Context_init_unchained(trustedForwarders);
    }

    // solhint-disable-next-line func-name-mixedcase
    function __MultiERC2771Context_init_unchained(address[] calldata trustedForwarders) internal initializer {
        for (uint i = 0; i < trustedForwarders.length; i++) {
            _trustedForwarders[trustedForwarders[i]] = true;
        }
    }

    // The overridden function should declare the appropriate access control//
    // keep init functions at the top by the constructor
    function _addForwarder(address forwarder) internal virtual {
        _trustedForwarders[forwarder] = true;
    }

    // The overridden function should declare the appropriate access control
    function _removeForwarder(address forwarder) internal virtual {
        _trustedForwarders[forwarder] = false;
    }

    function _msgSender() internal view virtual override returns (address sender) {
        if (isTrustedForwarder(msg.sender)) {
            // The assembly code is more direct than the Solidity version using `abi.decode`.
            // solhint-disable-next-line no-inline-assembly
            assembly {
                sender := shr(96, calldataload(sub(calldatasize(), 20)))
            }
        } else {
            return super._msgSender();
        }
    }

    function _msgData() internal view virtual override returns (bytes calldata) {
        if (isTrustedForwarder(msg.sender)) {
            return msg.data[:msg.data.length - 20];
        } else {
            return super._msgData();
        }
    }
}

